var gen = new MarkovGeneratorWord(2,16)
var nameGen = new MarkovGenerator(3, 20)
var totalCost = 0;
var totalStats = 0;
var mechanics = {"total":0}
for (var i = hsCards.length - 1; i >= 0; i--) {
	var card = hsCards[i];
	if(card.mechanics){
		for (var j = card.mechanics.length - 1; j >= 0; j--) {
			if(mechanics[card.mechanics[j]]){
				mechanics[card.mechanics[j]] = mechanics[card.mechanics[j]]+1
				mechanics.total++
			}
			else {
				mechanics[card.mechanics[j]] = 1
				mechanics.total++
			}
		}
	}
	if(card.attack && card.health && card.cost){
		totalStats += card.attack + card.health
		totalCost += card.cost
		if(card.text){
			gen.feed(card.text)
		}
		if(card.name){
			nameGen.feed(card.name)
		}
	}
}
function generate(){
	console.log(mechanics)
	var t = gen.generate();
	var n = nameGen.generate()
	tArray = t.split('.');
	if(tArray.length > 1){tArray.pop()}
	t = tArray.join('.');
	nArray = n.split(' ');
	if(nArray.length > 1){nArray.pop()}
	n = nArray.join(' ');
	$('p').html(t);
	$('#name').text(n)
	var cost = Math.ceil(Math.random()*10)
	var stats = cost * totalStats/totalCost
	if(Math.random() < 0.4){stats += 1}
	var attack = Math.floor(Math.random()*stats)
	var health = Math.ceil(stats - attack)
	while(Math.abs(attack - health) + 1 > cost){
		attack = Math.floor(Math.random()*stats)
		health = Math.ceil(stats - attack)
	}
	$('#cost').text(cost.toString())
	$('#atk').text(attack.toString())
	$('#health').text(health.toString())
}
function choice(arr){
	var i = Math.floor(Math.random()*arr.length);
	return arr[i]
}
var baseStats = {
	"2" : 2,
	"3" : 3,
	"4" : 5,
	"5" : 7,
	"6" : 9,
	"7" : 10,
	"8" : 12,
	"9" : 14,
	"10": 17
}

var abilitiesTable = {
	"1":["Taunt", "Stealth", "Freeze on damage"],
	"2":["Windfury", "Deal 1 damage", "Deathtouch"],
	"3":["Charge", "Divine Shield", "Silence an enemy minion"],
	"4":["Draw a card"]
}

function getCard(cost){
	var stats = baseStats[cost.toString()]
	var points = Math.floor(Math.random()*4)+ 1 + Math.floor(cost/3)
	var abilities = []
	while(points > 0){
		if(points > 4){
			var num = Math.ceil(Math.random() * 4)
		} else {
			var num = Math.ceil(Math.random() * points)
		}
		if(Math.random() < 0.5){
			abilities.push(abilitiesTable[num.toString()].choice())
		} else {
			stats += num
		}
		points -= num
	}
	var attack = Math.floor(Math.random()*stats)
	var health = Math.ceil(stats - attack)
	while(Math.abs(attack - health) + 1 > cost){
		attack = Math.floor(Math.random()*stats)
		health = Math.ceil(stats - attack)
	}
	$('#cost').text(cost.toString())
	$('#atk').text(attack.toString())
	$('#health').text(health.toString())
	$('p').html(abilities);
	var n = nameGen.generate()
	nArray = n.split(' ');
	if(nArray.length > 1){nArray.pop()}
	n = nArray.join(' ');
	$('#name').text(n)
}

function gen2(){
	if(Math.random()>0.5){
		generate()
	} else{
		getCard(Math.floor(Math.random()*9+2))
	}
}